import React, { useState, useEffect } from 'react';
import './App.css';
import './animate.css'
import {
  BrowserRouter as Router,
  Route, Switch
} from "react-router-dom";
import ScrollToTop from './components/ScrollToTop';
import Header from './components/Header';
import Home from './pages/Home';
import About from './pages/About';
import Portfolio from './pages/Portfolio';
import AllPosts from './components/AllPosts';
import BlogDetailsPage from './components/BlogDetailsPage';
import Contact from './pages/Contact';
import Footer from './components/Footer';
import MainShop from './pages/MainShop';
import Cart from './shop_component/Cart';
import ParentProductPage from './shop_component/ParentProductPage';
import Documentation from './components/Documentation';
import { NotFound } from 'http-errors';
//import CategoryPost from './components/CategoryPost';


function App() {
  const [loading, setLoading] = useState(true)
  //useEffect(() => {setTimeout(() => setLoading(false), 1000)}, [])
  if (loading) return <div><div class="loading">Loading&#8230;</div></div>
  return (
    <div className="App">
      <Router>
        <ScrollToTop>
          <Header />
          <Cart />
          <Switch>
          <Route path="/" exact>
            <Home />
          </Route>
          <Route path="/portfolio" exact>
            <Portfolio />
          </Route>
          <Route path="/about" exact>
            <About />
          </Route>
          <Route path="/contact" exact>
            <Contact />
          </Route>
          <Route path="/post/:slug" exact>
            <BlogDetailsPage />
          </Route>
          {/* <Route path="/category/:slug" exact>
          <CategoryPost />
        </Route> */}
          <Route path="/products/:handle" exact>
            <ParentProductPage />
          </Route>
          <Route path="/post/" exact>
            <AllPosts />
          </Route>
          <Route path="/shop" exact>
            <MainShop />
          </Route>
          <Route path="/doc" exact>
            <Documentation />
          </Route>
          <Route component={NotFound} />
          </Switch>
          <Footer />
        </ScrollToTop>
      </Router>
    </div>
  );
}

export default App;
