import '../assets/CSS/Cform.css';

class RegisterForm extends React.Component {
  constructor() {
    super();
    this.state = {
      fields: {},
      errors: {}
    }
    this.handleChange = this.handleChange.bind(this);
    this.submituserRegistrationForm = this.submituserRegistrationForm.bind(this);
  };
  handleChange(e) {
    let fields = this.state.fields;
    fields[e.target.name] = e.target.value;
    this.setState({
      fields
    });

  }
  formClear = () => {
    this.setState({ fields: {} })
  }
  submituserRegistrationForm(e) {
    e.preventDefault();

    if (this.validateForm()) {
      let fields = {};
      fields["txtName"] = "";
      fields["txtEmail"] = "";
      fields["txtMessage"] = "";
      // fields["mobileno"] = "";
      // fields["password"] = "";
      this.setState({ fields: fields });
      alert("Form submitted");
    }
  }

  validateForm() {

    let fields = this.state.fields;
    let errors = {};
    let formIsValid = true;

    if (!fields["txtName"]) {
      formIsValid = false;
      errors["txtName"] = "*Please enter your Name.";
    }

    if (typeof fields["txtName"] !== "undefined") {
      if (!fields["txtName"].match(/^[a-zA-Z ]*$/)) {
        formIsValid = false;
        errors["txtName"] = "*Please enter alphabet characters only.";
      }
    }


    if (!fields["txtMessage"]) {
      formIsValid = false;
      errors["txtMessage"] = "*Please enter your Message.";
    }


    if (!fields["txtEmail"]) {
      formIsValid = false;
      errors["txtEmail"] = "*Please enter your email-ID.";
