import { InventoryShape } from '../types';
declare type BigCommerceInventory = Record<string, InventoryShape & {
    inventoryTracking: boolean;
}>;
declare type BigCommerceRESTCatalog = {
    id: number;
    price: number;
    inventory_level: number;
    inventory_warning_level: number;
    inventory_tracking: 'none' | 'product' | 'variant';
    availability: 'available' | 'disabled' | 'preorder';
    availability_description: string;
    order_quantity_minimum: number;
    order_quantity_maximum: number;
    is_preorder_only: boolean;
    is_price_hidden: boolean;
    price_hidden_label: string;
}[];
export { BigCommerceInventory, BigCommerceRESTCatalog };
