import { InventoryShape } from '../types';
declare type ShopifyInventory = Record<string, InventoryShape>;
declare type CMSInventoryItem = Record<string, {
    minOrder?: number;
    maxOrder?: number;
}>;
declare type CMSInventory = {
    products?: CMSInventoryItem;
    productVariants?: CMSInventoryItem;
};
declare type ShopifyInventoryResponse = {
    nodes: {
        id: string;
        availableForSale: boolean;
        quantityAvailable: number;
        price: number;
    }[];
};
export { CMSInventory, ShopifyInventory, ShopifyInventoryResponse };
